INSERT INTO scholarships (name, foundation, category, benefit_type, payment_method, payment_period, extra_benefits, target_hashtags, target_description, eligibility, selection_count, application_count, application_period, application_end, application_method, required_documents, description, contact, link, amount, target_grade, min_gpa, max_income, target_gender, target_school_type, target_region, target_major_category, min_prev_semester_credits, special_criteria, target_nationality, target_parents_region, target_university_region, target_high_school_region, max_csat_grade, max_school_grade, target_enrollment_status, target_universities) VALUES ('2026년 제13기 운해장학생', '운해장학재단', '민간장학', '학업장려금', '2월 말과 8월 말에 학생계좌 직접 입금', '당해연도 2개학기', '없음', '{"대학생"}', '2025년 12월 현재 기준 재학중인 대학교 1,2,3학년생
※ 2026년도 1학기 성적증명서 및 2학기 등록서류를 제출할 수 없는 경우 신청 불가(복학생, 전학, 휴학, 해외 교환학생, 군입대 및 해외연수 예정자 등) 
※ 5년제 또는 6년제 전공을 하는 경우는 4학년생 신청 가능', '1) 이공계 전문 학생(2025년 1학기 평점평균 기준 이상)으로 자기 전문분야에서 탁월한 능력이 있다고 판단되는 학생으로 대학교 총장의 추천을 받은 학생 
2) 학업성적이 우수(2025년 1학기 평점평균 기준 이상)하나 가정형편 때문에 학업에 곤란함을 겪는 학생으로 대학교 총장의 추천을 받은 학생 
3) 평점평균 기준: 4.0/4.5 이상 (또는 3.82/4.3, 3.56/4.0 이상)', '650명', '연 1회', 45987, '2025-12-26', '우편 접수 (12월 26일자 우체국 소인까지 유효)', '신청지원서, 자기소개서 및 학업계획서, 성적증명서, 부모의 2025년도분 지방세 세목별 과세증명서, 주민등록등본 또는 가족관계증명서, 대학교 총장의 추천서 등', '이공계 전공 학생으로 자기 전문분야에서 탁월한 능력이 있다고 판단되거나 학업성적 우수하나 어려운 가정형편 때문에 학업에 곤란함을 겪는 학생을 대상으로 장학금을 지원', '운해장학재단 (055-280-5145)', 'https://woonhaefoundation.org/kor/information/scholarship_notice.html?code=sub04_01&p=&subcode=&page=1&bbsData=bm89MTQyMTE=||&mode=view', '연간 800만원', '1,2,3,4', 4, 10, 'any', 'university', '전국', '공학', 0, NULL, 'Korea', '전국', '전국', '전국', 9, 9, 'enrolled', '무관');
INSERT INTO scholarships (name, foundation, category, benefit_type, payment_method, payment_period, extra_benefits, target_hashtags, target_description, eligibility, selection_count, application_count, application_period, application_end, application_method, required_documents, description, contact, link, amount, target_grade, min_gpa, max_income, target_gender, target_school_type, target_region, target_major_category, min_prev_semester_credits, special_criteria, target_nationality, target_parents_region, target_university_region, target_high_school_region, max_csat_grade, max_school_grade, target_enrollment_status, target_universities) VALUES ('2026 해동장학생', '해동과학문화재단', '민간장학', '학업장려금', '매월 100만원 분할 지급', '당해연도 (단, 조건을 만족하는 경우 졸업시까지 유지)', '네트워킹 프로그램 (나눔소모임, 송년회, 산업체시찰 등) 및 일본 산업관련 정보제공 등', '{"대학생"}', '재단 지원 대학교 내 다음 조건을 만족하는 학생을 대상으로 함
※ 대상학교: 경북대, 광운대, 고려대, 국민대, 부산대, 성균관대, 아주대, 이화여대, 인하대, 연세대, 서강대, 서울대, 서울과기대, 전북대, 한양대, 홍익대', '1) 2026년 3월 기준, 2학년 1학기, 2학년 2학기, 3학년 1학기 진학 예정자
2) 화학공학, 재료공학, 전자공학, 신소재공학, 반도체공학, 컴퓨터공학(인공지능 전공 포함) 관련분야 전공자
3) 한국장학재단 소득분위 4분위 이내인 자
4) 연속으로 두 학기 이상 학업이 지속 가능하며, 생활비·학업장려금 성격의 장학금을 중복수혜하지 않는 자', '00명', '연 1회', 45993, '2026-01-08', '각 대학 장학 담당처', '신청서, 자기소개서, 추천서, 성적증명서 (4.5점 기준, 2025년 2학기 포함), 한국장학재단 소득분위 (2025년 2학기 기준), 개인정보 수집이용제공 동의서, 장학금수혜증명서 (최종합격시 2026년 4월경 제출)', '면학의욕이 높고, 학업성적이 우수하나 경제적 여건으로 학업에 어려움을 겪고 있는 학생을 지원하여, 훌륭한 인재로 육성', '해동과학문화재단 (02-521-2711)', 'https://www.yonsei.ac.kr/bbs/sc/58/942063/artclView.do', '연간 1,200만원', '2,3', 0, 4, 'any', 'university', '전국', '공학', 0, NULL, 'Korea', '전국', '전국', '전국', 9, 9, 'enrolled', '경북대,광운대,고려대,국민대,부산대,성균관대,아주대,이화여대,인하대,연세대,서강대,서울대,서울과기대,전북대,한양대,홍익대');
