INSERT INTO scholarships (name, foundation, category, benefit_type, payment_method, payment_period, extra_benefits, target_hashtags, target_description, eligibility, selection_count, application_count, application_period, application_end, application_method, required_documents, description, contact, link, amount, target_grade, min_gpa, max_income, target_gender, target_school_type, target_region, target_major_category, min_prev_semester_credits, special_criteria, target_nationality, target_parents_region, target_university_region, target_high_school_region, max_csat_grade, max_school_grade, target_enrollment_status, target_universities) VALUES ('2026학년도 강남장학회 장학생', '원불교 강남교당, 강남장학회', '민간장학', '복합지원', '학기별 분할 지급', '당해연도 2개학기', '청년마음훈련캠프(1월&7월, 훈련비 일부지원), 청년활동 프로젝트 1회, 장학생 독서모임 및 마음공부 격주 진행', '{"대학생","대학원생"}', '서울, 경기 소재 대학교, 대학원생', '서울, 경기 소재 대학생 및 대학원생으로 2026 겨울청년마음훈련 필참 가능한 자', '12명', '연 1회', 45995, '2025-12-26', '이메일 제출 (won204-19@naver.com)', '신청서, 자기소개서, 개인정보 활용동의서, 재학증명서, 직전학기까지의 성적증명서 등', '[마음을 알고, 마음을 지키고, 마음을 잘 사용하는 인재를 만드는, 강남장학회 18기]
