INSERT INTO scholarships (name, foundation, category, benefit_type, payment_method, payment_period, extra_benefits, target_hashtags, target_description, eligibility, selection_count, application_count, application_period, application_end, application_method, required_documents, description, contact, link, amount, target_grade, min_gpa, max_income, target_gender, target_school_type, target_region, target_major_category, min_prev_semester_credits, special_criteria, target_nationality, target_parents_region, target_university_region, target_high_school_region, max_csat_grade, max_school_grade, target_enrollment_status, target_universities) VALUES ('2026년 아르코문학작가펠로우십', '한국문화예술위원회', '민간장학', '복합지원', '선정 후 2개월 이내 선지급 80%, 사업 결과 확인 후 잔여 20% 지급', '당해연도', '연계 지원 및 후속 지원 (공고 및 연계사업 설명자료 참조)', '{"대학생","대학원생","일반인"}', '다음의 신청 자격 3개 중 2개 이상을 충족하는 문학작가
※ 지원신청서에 기재한 계획의 실행, 한국문화예술위원회 연계 지원사업에 최소 1건 이상 참여, 연내 결과보고서 제출 필수 조건', '1) 최근 10년 내(2015.12.30~2025.12.29) 한국문화예술위원회 및 광역/민간문화재단의 작품집 발간 지원사업에 선정된 이력이 있는 작가
2) 최근 10년 내(2015.12.30~2025.12.29) 국내외 주요 문학상 수상 이력이 있는 작가 (신인문학상, 신춘문예 등 최초 등단 문학상의 경우는 불인정)
3) 신청 분야에서 창작활동을 시작 후 최근 10년 내 ISBN이 부여된 단행본을 2권 이상 출간한 작가 (모든 활동은 최근 10년 내 이력을 기준으로 하며, 등단 연차와는 무관함)', '30명', '연 1회', 45992, '2025-12-29', '국가문화예술지원시스템(www.ncas.or.kr)에서 온라인 신청', '2026년도 문예진흥기금 지원신청서', '문학계에서 꾸준한 성과를 보여온 작가를 집중 지원하는 사업으로, 연간 활동지원금 및 다양한 후속 활동을 지원하여 예술적 성장과 공공적 기여 촉진', '한국문화예술위원회 문학지원팀 (061-900-2326)', 'https://artnuri.or.kr/crawler/info/view.do?docid=CRL3292&source=ARKO&pageSetting=&sc_seNo=&key=2301170002&sc_orderBy=&recordCountPerPage=10&pageIndex=1&sc_hash=&sc_list=&seNo=001&sw=aRKO&sc_genre=&sc_target=&sc_local=&sc_field=&sc_isDo=', '2,000만원', '1,2,3,4', 0, 10, 'any', 'university, college, grad_school, cyber, open', '전국', '무관', 0, NULL, 'Korea', '전국', '전국', '전국', 9, 9, 'enrolled', '무관');
